1: :root {
2:     /* Dark mode color variables */
3:     --color-bg-panel: rgba(255, 255, 255, 0.1);
4:     --color-bg-intro: rgba(255, 255, 255, 0.1);
5:     --color-bg-section-header: rgba(255, 255, 255, 0.05);
6:     --color-bg-section-header-hover: rgba(255, 255, 255, 0.1);
7:     --color-bg-section-content: rgba(0, 0, 0, 0.2);
8:     --color-bg-method-icon: rgba(255, 255, 255, 0.1);
9:     --color-bg-tip-card: rgba(255, 255, 255, 0.05);
10: 
11:     --color-border-section: rgba(255, 255, 255, 0.1);
12:     --color-border-section-header-expanded: rgba(255, 255, 255, 0.1);
13:     --color-border-method: rgba(255, 255, 255, 0.1);
14: 
15:     --color-title: #4dabf7;
16:     --color-header: #fff;
17:     --color-method-title: #4dabf7;
18:     --color-footer: #4dabf7;
19: }
20: 
21: .help-panel {
22:     max-height: 100%;
23:     padding-bottom: 20px;
24: }
25: 
26: /* Introduction styling */
27: .help-intro {
28:     margin-bottom: 20px;
29:     padding: 12px;
30:     background-color: var(--color-bg-intro);
31:     border-radius: 8px;
32: }
33: 
34: .welcome-text {
35:     font-size: 1.1em;
36:     margin-bottom: 10px;
37: }
38: 
39: .app-title {
40:     font-weight: bold;
41:     color: var(--color-title);
42:     font-size: 1.2em;
43: }
44: 
45: .intro-description {
46:     line-height: 1.4;
47: }
48: 
49: /* Collapsible section styling */
50: .collapsible-section {
51:     margin-bottom: 10px;
52:     border: 1px solid var(--color-border-section);
53:     border-radius: 8px;
54:     overflow: hidden;
55: }
56: 
57: .section-header {
58:     display: flex;
59:     justify-content: space-between;
60:     align-items: center;
61:     padding: 12px 15px;
62:     background-color: var(--color-bg-section-header);
63:     cursor: pointer;
64:     transition: background-color 0.2s ease;
65: }
66: 
67: .section-header:hover {
68:     background-color: var(--color-bg-section-header-hover);
69: }
70: 
71: .section-header h3 {
72:     margin: 0;
73:     font-size: 1.1em;
74:     color: var(--color-header);
75: }
76: 
77: .toggle-icon {
78:     font-size: 1.5em;
79:     color: var(--color-title);
80:     font-weight: bold;
81: }
82: 
83: .section-header.expanded {
84:     border-bottom: 1px solid var(--color-border-section-header-expanded);
85: }
86: 
87: .section-content {
88:     padding: 15px;
89:     background-color: var(--color-bg-section-content);
90: }
91: 
92: /* Method icons and content */
93: .help-method {
94:     display: flex;
95:     align-items: flex-start;
96:     margin-bottom: 16px;
97:     padding-bottom: 16px;
98:     border-bottom: 1px dashed var(--color-border-method);
99: }
100: 
101: .help-method:last-child {
102:     margin-bottom: 0;
103:     padding-bottom: 0;
104:     border-bottom: none;
105: }
106: 
107: .method-icon {
108:     width: 30px;
109:     height: 30px;
110:     border-radius: 50%;
111:     margin-right: 15px;
112:     flex-shrink: 0;
113:     background-color: var(--color-bg-method-icon);
114:     background-position: center;
115:     background-repeat: no-repeat;
116:     background-size: 60%;
117: }
118: 
119: .text-search {
120:     background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="%234dabf7" stroke-width="2"><circle cx="11" cy="11" r="8"/><line x1="21" y1="21" x2="16.65" y2="16.65"/></svg>');
121: }
122: 
123: .magic-wand {
124:     background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="%234dabf7" stroke-width="2"><path d="M15 4V2m0 20v-2M4 15H2m20 0h-2M5.6 5.6l-1.4-1.4m14.2 0l-1.4 1.4m0 12.8l1.4 1.4M5.6 18.4l-1.4 1.4M12 17l5-5a2.83 2.83 0 000-4 2.83 2.83 0 00-4 0l-5 5v4h4z"/></svg>');
125: }
126: 
