129: }
130: 
131: .special-shapes {
132:     background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="%234dabf7" stroke-width="2"><path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/></svg>');
133: }
134: 
135: .history {
136:     background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="%234dabf7" stroke-width="2"><circle cx="12" cy="12" r="10"/><polyline points="12 6 12 12 16 14"/></svg>');
137: }
138: 
139: .method-content h4 {
140:     margin-top: 0;
141:     margin-bottom: 6px;
142:     color: var(--color-method-title);
143: }
144: 
145: .method-content p {
146:     margin: 0;
147:     line-height: 1.4;
148: }
149: 
150: /* Tip cards */
151: .tip-card {
152:     display: flex;
153:     align-items: center;
154:     margin-bottom: 12px;
155:     padding: 10px;
156:     border-radius: 6px;
157:     background-color: var(--color-bg-tip-card);
158: }
159: 
160: .tip-icon {
161:     width: 24px;
162:     height: 24px;
163:     margin-right: 12px;
164:     flex-shrink: 0;
165:     background-position: center;
166:     background-repeat: no-repeat;
167:     background-size: contain;
168: }
