170: .drag-icon {
171:     background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="%234dabf7" stroke-width="2"><path d="M16 13v-2H7a4 4 0 00-4 4 4 4 0 004 4h12a4 4 0 004-4 4 4 0 00-4-4h-3zm0 0V7"/></svg>');
172: }
173: 
174: .scale-icon {
175:     background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="%234dabf7" stroke-width="2"><path d="M21 16V8a2 2 0 00-1-1.73l-7-4a2 2 0 00-2 0l-7 4A2 2 0 003 8v8a2 2 0 001 1.73l7 4a2 2 0 002 0l7-4A2 2 0 0021 16z"/><polyline points="3.27 6.96 12 12.01 20.73 6.96"/><line x1="12" y1="22.08" x2="12" y2="12"/></svg>');
176: }
177: 
178: .history-icon {
179:     background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="%234dabf7" stroke-width="2"><circle cx="12" cy="12" r="10"/><polyline points="12 6 12 12 16 14"/></svg>');
180: }
181: 
182: /* About section */
183: .about-section p {
184:     margin-bottom: 15px;
185:     line-height: 1.5;
186: }
187: 
188: .about-section p:last-child {
189:     margin-bottom: 0;
190: }
191: 
192: /* Footer */
193: .help-footer {
194:     margin-top: 20px;
195:     text-align: center;
196:     padding: 10px;
197:     font-style: italic;
198:     color: var(--color-footer);
199: }
